.card-container .card
{
	position: relative;
	width: 320px;
	margin: 30px;
	height:440px;
	box-shadow: inset 5px 5px 5px rgba(0,0,0,0.05),
                inset -5px -5px 5px rgba(255,255,255,0.5),
                5px 5px 5px rgba(0,0,0,0.05),
                -5px -5px 5px rgba(255,255,255,0.5);
    border-radius: 15px; 
} 
.card-container .card .card-box{
	position: absolute;
	top: 20px;
	left: 20px;
	right:20px;
	bottom: 20px;
	background-color: #fff;
	border-radius: 15px;
	box-shadow:  0 10px 40px rgba(0,0,0,0.3);
	display: flex;
	justify-content: center;
	align-items: center;
	transition: 0.5s;
}
.card-container .card:hover .card-box{
	transform:  translateY(-50px);
	box-shadow:  0 10px 40px rgba(0,0,0,0.4);
	background: linear-gradient(45deg, #b95ce4, #4f29cd);
}
.card-container .card .card-box .card-content{
	padding: 20px;
	text-align:center;
}
.card-container .card .card-box .card-content h2{
	position: absolute;
	top: -10px;
	right: 30px;
	font-size: 8em;
	color: rgba(0,0,0,0.05);
	transition: 0.5s;
	pointer-events: none;
}
.card-container .card:hover .card-box .card-content h2{
	color: #fff;
}
.card-container .card .card-box .card-content h3{
	font-size: 1.8em;
	color: #777;
	z-index: 1;
	transition: 0.5s;
}
.card-container .card:hover .card-box .card-content h3{
	color: #fff;
}
.card-container .card .card-xox .card-content p{
	font-size: 1em;
	font-weight: 300;
	color: #777;
	z-index: 1;
	transition: 0.5s;
}
.card-container .card:hover .card-box .card-content p{
	color: #fff;
}
.card-container .card .card-box .card-content a{
	position: relative;
	display: inline-block;
	padding: 8px 20px;
	background-color: #03a9f4;
	margin-top: 15px;
	border-radius: 20px;
	color: #fff;
	text-decoration: none;
	font-weight: 400;
